function g(r,o){return a("local",r,o)}function u(r,o){return s("local",r,o)}function f(r){return i("local",r)}function a(r,o,t){try{const e=c(r);if(!e)return t;const n=e.getItem(o);return typeof n!="string"?t:JSON.parse(n).val||t}catch(e){return console.error(e),t}}function s(r,o,t){try{const e=c(r);if(!e)return;const n={val:t};e.setItem(o,JSON.stringify(n))}catch(e){console.error(e)}}function i(r,o){try{const t=c(r);if(!t)return;t.removeItem(o)}catch(t){console.error(t)}}function c(r){return r==="local"?window.localStorage:r==="session"?window.sessionStorage:null}export{g,f as r,u as s};
